import React, { useState, useEffect } from 'react';
import TrailCard from '../../components/TrailCard/TrailCard';
import Button from '../../components/Button/button';
import heroBackground from '../../images/mountains.jpg';
import { featuredTrails } from '../../data/data';
import { locationMapping } from '../../data/location_mapping'; // Import the location mapping
import './Home.css';

const Home = () => {
  const [showAll, setShowAll] = useState(false);
  const [userLocation, setUserLocation] = useState('');
  const [filteredTrails, setFilteredTrails] = useState([]);

  useEffect(() => {
    // Get the user's location using the Geolocation API
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(position => {
        const { latitude, longitude } = position.coords;
        fetch(`https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${latitude}&longitude=${longitude}&localityLanguage=en`)
          .then(response => response.json())
          .then(data => {
            const city = data.city || data.locality || data.principalSubdivision || "Your Location";
            setUserLocation(city);

            // Use locationMapping to get relevant hikes based on the user's city
            const nearbyLocations = locationMapping[city] || [];
            const trailsInCity = featuredTrails.filter(trail => nearbyLocations.includes(trail.location));
            setFilteredTrails(trailsInCity);
          });
      }, () => {
        setUserLocation("Your Location");
        setFilteredTrails(featuredTrails); // fallback to all trails if location access is denied
      });
    } else {
      setUserLocation("Your Location");
      setFilteredTrails(featuredTrails); // fallback if geolocation is not supported
    }
  }, []);

  const displayedTrails = showAll ? filteredTrails : filteredTrails.slice(0, 4);

  return (
    <div className="home-container">
      {/* Hero Section */}
      <div className="hero-section" style={{ backgroundImage: `url(${heroBackground})` }}>
        <div className="hero-content">
          <h1>Discover Your Next Adventure in {userLocation}</h1>
          <p>Join our community of hikers and find the perfect trail</p>

          {/* Search Section */}
          <div className="search-container">
            <input
              type="text"
              placeholder="Search for trails..."
              className="search-input"
            />
          </div>
        </div>
      </div>

      {/* Trails Section */}
      <div className="trails-section">
        <h2>Popular Trails near {userLocation}</h2>

        <div className="trails-grid">
          {displayedTrails.length > 0 ? (
            displayedTrails.map(trail => (
              <TrailCard key={trail.id} trail={trail} />
            ))
          ) : (
            <p>No trails found in your area.</p>
          )}
        </div>

        {/* Show More button */}
        {filteredTrails.length > 4 && (
          <div className="show-more-container">
            <Button
              variant="outline"
              onClick={() => setShowAll(!showAll)}
              className="show-more-button"
            >
              {showAll ? 'Show Less' : 'Show More'}
            </Button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
