import React, { useState, useEffect } from 'react';
import TrailCard from '../../components/TrailCard/TrailCard';
import Button from '../../components/Button/button';
import CitySelector from '../../components/CitySelector/Cityselector';
import './Home.css';
import { locationMapping } from '../../data/location_mapping';

const Home = () => {
  const [showAll, setShowAll] = useState(false);
  const [location, setLocation] = useState('');
  const [trails, setTrails] = useState([]);
  const [cityOpen, setCityOpen] = useState(false);
  const [errorMsg, setErrorMsg] = useState('');
  const [search, setSearch] = useState('');

  useEffect(() => {
    const getTrails = async (city) => {
      try {
        const res = await fetch(`http://localhost:8000/api/trails?city=${city}`);
        if (!res.ok) throw new Error('Failed to fetch');
        const data = await res.json();
        setTrails(data);
      } catch (error) {
        setErrorMsg('Could not load trails. Try again later.');
      }
    };

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(pos => {
        const { latitude, longitude } = pos.coords;
        fetch(`https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${latitude}&longitude=${longitude}&localityLanguage=en`)
          .then(response => response.json())
          .then(data => {
            const city = data.city || data.locality || data.principalSubdivision || "Your Location";
            setLocation(city);
            getTrails(city);
          });
      }, () => {
        setCityOpen(true);
      });
    } else {
      setCityOpen(true);
    }
  }, []);

  const selectCity = async (city) => {
    setLocation(city);
    const res = await fetch(`http://localhost:8000/api/trails?city=${city}`);
    const data = await res.json();
    setTrails(data);
    setCityOpen(false);
  };

  const filteredTrails = trails
    .filter(trail => trail.name.toLowerCase().includes(search.toLowerCase()))
    .slice(0, showAll ? undefined : 4);

  return (
    <div className="home">
      <div className="hero" style={{ backgroundImage: `url(http://localhost:8000/images/hiking.jpg)` }}>
        <div className="hero-text">
          <h1>Find Your Next Adventure</h1>
          <p>Join the hiking community and discover amazing trails</p>

          <div className="search">
            <input
              type="text"
              placeholder="Search trails..."
              className="search-input"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
          </div>
        </div>
      </div>

      <div className="trails">
        <h2>Trails in {location}</h2>
        {errorMsg && <p className="error">{errorMsg}</p>}

        <div className="trail-list">
          {filteredTrails.length > 0 ? (
            filteredTrails.map(trail => (
              <TrailCard key={trail._id} trail={trail} />
            ))
          ) : (
            <p>No trails available</p>
          )}
        </div>

        {trails.length > 4 && (
          <div className="show-more">
            <Button onClick={() => setShowAll(!showAll)}>
              {showAll ? 'Show Less' : 'Show More'}
            </Button>
          </div>
        )}
      </div>

      {cityOpen && (
        <div className="modal">
          <CitySelector
            cities={Object.keys(locationMapping)}
            onSelectCity={selectCity}
            onClose={() => setCityOpen(false)}
          />
        </div>
      )}
    </div>
  );
};

export default Home;
