// src/components/TrailCard/TrailCard.js
import React from 'react';
import { useHistory } from 'react-router-dom';
import './TrailCard.css';

const TrailCard = ({ trail }) => {
  const history = useHistory();

  const handleClick = () => {
    history.push(`/trails/${trail.id}`);
  };

  return (
    <div className="trail-card" onClick={handleClick} style={{ cursor: 'pointer' }}>
      <div className="trail-card-image">
        <img src={trail.image} alt={trail.name} />
        <div className="trail-difficulty">{trail.difficulty}</div>
      </div>
      <div className="trail-card-content">
        <h3>{trail.name}</h3>
        <div className="trail-info">
          <p className="location">üìç {trail.location}</p>
          <p className="length">‚ö° {trail.length}</p>
          <p className="elevation">üèî {trail.elevation}</p>
        </div>
      </div>
    </div>
  );
};

export default TrailCard;
