// src/pages/TrailDetails/TrailDetails.js
import React from 'react';
import { useParams } from 'react-router-dom';
import { featuredTrails } from '../../data/data';
import './TrailDetails.css';

const TrailDetails = () => {
  const { id } = useParams();
  const trail = featuredTrails.find(trail => trail.id === parseInt(id));

  if (!trail) return <p className="not-found">Trail not found</p>;

  return (
    <div className="trail-details">
      <h1 className="trail-title">{trail.name}</h1>

      <div className="image-carousel">
        {trail.images.map((image, index) => (
          <img key={index} src={image} alt={`${trail.name} ${index + 1}`} className="carousel-image" />
        ))}
      </div>

      <div className="trail-info">
        <p><strong>Location:</strong> {trail.location}</p>
        <p><strong>Difficulty:</strong> {trail.difficulty}</p>
        <p><strong>Length:</strong> {trail.length}</p>
        <p><strong>Elevation:</strong> {trail.elevation}</p>
      </div>

      <section className="reviews-section">
        <h2>Customer Reviews</h2>
        {trail.reviews && trail.reviews.length > 0 ? (
          trail.reviews.map(review => (
            <div key={review.id} className="review">
              <p><strong>{review.user}</strong> - {review.rating} stars</p>
              <p>{review.comment}</p>
            </div>
          ))
        ) : (
          <p className="review-placeholder">No reviews yet. Be the first to add one!</p>
        )}
      </section>
    </div>
  );
};

export default TrailDetails;
