import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './TrailDetails.css';

const TrailDetails = () => {
  const { id } = useParams();
  const [trail, setTrail] = useState(null);
  const [currentImg, setCurrentImg] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showReview, setShowReview] = useState(false);
  const [reviewText, setReviewText] = useState('');
  const [rating, setRating] = useState(5);

  useEffect(() => {
    const getTrail = async () => {
      try {
        const res = await fetch(`http://localhost:8000/api/trails/${id}`);
        if (!res.ok) throw new Error('Error fetching trail');
        const data = await res.json();
        setTrail(data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching trail:', err);
        setError(err.message);
        setLoading(false);
      }
    };

    getTrail();
  }, [id]);

  const prevImg = () => {
    if (trail?.images?.length) {
      setCurrentImg((prev) => (prev === 0 ? trail.images.length - 1 : prev - 1));
    }
  };

  const nextImg = () => {
    if (trail?.images?.length) {
      setCurrentImg((prev) => (prev === trail.images.length - 1 ? 0 : prev + 1));
    }
  };

  const addReview = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert("Please log in to add a review.");
      return;
    }
    setShowReview(true);
  };

  const submitReview = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    if (!token) {
      alert("Please log in to submit a review.");
      return;
    }

    try {
      const res = await fetch(`http://localhost:8000/api/trails/${id}/reviews`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          text: reviewText,
          rating: parseInt(rating)
        })
      });

      if (!res.ok) {
        const errorData = await res.json();
        throw new Error(errorData.message || 'Failed to submit review');
      }

      const updatedTrail = await res.json();
      setTrail(updatedTrail);
      setShowReview(false);
      setReviewText('');
      setRating(5);
      alert('Review submitted successfully!');
    } catch (err) {
      console.error('Error submitting review:', err);
      alert(err.message || 'Error submitting review. Please try again.');
    }
  };

  const deleteReview = async (reviewId) => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert("Please log in to delete a review.");
      return;
    }

    if (!window.confirm('Are you sure you want to delete this review?')) {
      return;
    }

    try {
      const res = await fetch(`http://localhost:8000/api/trails/${id}/reviews/${reviewId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!res.ok) {
        const errorData = await res.json();
        throw new Error(errorData.message || 'Failed to delete review');
      }

      const updatedTrail = await res.json();
      setTrail(updatedTrail);
      alert('Review deleted successfully!');
    } catch (err) {
      console.error('Error deleting review:', err);
      alert(err.message || 'Error deleting review. Please try again.');
    }
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!trail) return <div className="not-found">Trail not found</div>;

  const currentUserId = localStorage.getItem('userId');

  return (
    <div className="trail-detail">
      <h1 className="title">{trail.name}</h1>

      {trail.images && trail.images.length > 0 && (
        <div className="carousel">
          <button className="arrow left" onClick={prevImg}>&lt;</button>
          <img
            src={`http://localhost:8000${trail.images[currentImg]}`}
            alt={`${trail.name} view ${currentImg + 1}`}
            className="carousel-img"
          />
          <button className="arrow right" onClick={nextImg}>&gt;</button>
          <div className="image-counter">
            {currentImg + 1} / {trail.images.length}
          </div>
        </div>
      )}

      <div className="info">
        <p><strong>Location:</strong> {trail.location}</p>
        <p><strong>Difficulty:</strong> {trail.difficulty}</p>
        <p><strong>Length:</strong> {trail.length}</p>
        <p><strong>Elevation:</strong> {trail.elevation}</p>
        {trail.averageRating > 0 && (
          <p><strong>Average Rating:</strong> {trail.averageRating} / 5</p>
        )}
      </div>

      <div className="reviews-section">
        <div className="reviews-header">
          <h2>Reviews</h2>
          <button onClick={addReview} className="add-review-btn">
            Add Review
          </button>
        </div>

        {showReview && (
          <div className="review-form">
            <h3>Write a Review</h3>
            <form onSubmit={submitReview}>
              <div className="form-group">
                <label htmlFor="rating">Rating:</label>
                <input
                  id="rating"
                  type="number"
                  min="1"
                  max="5"
                  value={rating}
                  onChange={(e) => setRating(Number(e.target.value))}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="review">Review:</label>
                <textarea
                  id="review"
                  value={reviewText}
                  onChange={(e) => setReviewText(e.target.value)}
                  required
                  rows="4"
                  placeholder="Write your review here..."
                />
              </div>
              <div className="form-buttons">
                <button type="submit" className="submit-btn">Submit</button>
                <button 
                  type="button" 
                  onClick={() => setShowReview(false)} 
                  className="cancel-btn"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        )}

        <div className="reviews-list">
          {trail.reviews && trail.reviews.length > 0 ? (
            trail.reviews.map((review) => (
              <div key={review._id} className="review-card">
                <div className="review-header">
                  <strong>{review.user}</strong>
                  <span className="review-rating">Rating: {review.rating}/5</span>
                </div>
                <p className="review-text">{review.text}</p>
                <div className="review-footer">
                  <span className="review-date">
                    {new Date(review.date).toLocaleDateString()}
                  </span>
                  {review.userId === currentUserId && (
                    <button 
                      onClick={() => deleteReview(review._id)}
                      className="delete-btn"
                    >
                      Delete Review
                    </button>
                  )}
                </div>
              </div>
            ))
          ) : (
            <p className="no-reviews">No reviews yet. Be the first to review!</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default TrailDetails;
