import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';

const Navbar = ({ isLoggedIn, onLogout }) => {
  const [isHovered, setIsHovered] = useState(false);  // Track if the profile picture is hovered
  const navigate = useNavigate();

  const handleProfileClick = () => {
    // You can handle profile options here, for now, it’s a simple alert
    alert('User Profile Clicked');
  };

  const handleLogout = () => {
    onLogout();
    navigate('/login'); // Redirect to login page after logout
  };

  return (
    <nav className="navbar">
      <div className="container navbar-container">
        <Link to="/" className="navbar-logo">
          <i className="fas fa-mountain"></i>
          TrailMates
        </Link>
        <div className="navbar-links">
          <Link to="/" className="navbar-link">Home</Link>
          <Link to="/community" className="navbar-link">Community</Link>
          <Link to="/shop" className="navbar-link">Shop</Link>
          {isLoggedIn ? (
            <div 
              className="navbar-profile"
              onMouseEnter={() => setIsHovered(true)} // Show logout button on hover
              onMouseLeave={() => setIsHovered(false)} // Hide logout button when hover ends
            >
              <img
                src="https://via.placeholder.com/30" // Default profile picture
                alt="Profile"
                className="profile-img"
                onClick={handleProfileClick}
              />
              {isHovered && (
                <button className="logout-button" onClick={handleLogout}>
                  Logout
                </button>
              )}
            </div>
          ) : (
            <Link to="/login" className="navbar-link">Login</Link>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
