// src/pages/Home/Home.js
import React from 'react';
import TrailCard from '../../components/TrailCard/TrailCard';
import './Home.css';

const Home = () => {
  const featuredTrails = [
    {
      id: 1,
      name: "Mountain Peak Trail",
      difficulty: "Moderate",
      length: "5.2 miles",
      image: "/assets/images/trail1.jpg"
    },
    {
      id: 2,
      name: "Forest Loop",
      difficulty: "Easy",
      length: "3.1 miles",
      image: "/assets/images/trail2.jpg"
    },
    {
      id: 3,
      name: "Valley Vista",
      difficulty: "Hard",
      length: "8.7 miles",
      image: "/assets/images/trail3.jpg"
    }
  ];

  return (
    <main>
      <section className="hero">
        <div className="container">
          <h1>Discover Your Next Adventure</h1>
          <p>Join our community of hikers and find the perfect trail</p>
          <div className="search-container">
            <input
              type="text"
              className="search-input"
              placeholder="Search for trails..."
            />
          </div>
        </div>
      </section>

      <section className="featured-trails container">
        <h2>Featured Trails</h2>
        <div className="trails-grid">
          {featuredTrails.map(trail => (
            <TrailCard key={trail.id} trail={trail} />
          ))}
        </div>
      </section>
    </main>
  );
};

export default Home;
