import React, { useState, useEffect } from 'react';
import TrailCard from '../../components/TrailCard/TrailCard';
import Button from '../../components/Button/button';
import CitySelector from '../../components/CitySelector/Cityselector';
import './Home.css';
import { locationMapping } from '../../data/location_mapping';

const Home = () => {
  const [showAll, setShowAll] = useState(false);
  const [userLocation, setUserLocation] = useState('');
  const [filteredTrails, setFilteredTrails] = useState([]);
  const [citySelectorOpen, setCitySelectorOpen] = useState(false);
  const [error, setError] = useState(''); // State for error handling

  useEffect(() => {
    const fetchTrails = async (city) => {
      try {
        const response = await fetch(`http://localhost:8000/api/trails?city=${city}`);
        if (!response.ok) throw new Error('Network response was not ok');
        const trails = await response.json();
        setFilteredTrails(trails);
      } catch (error) {
        console.error('Error fetching trails:', error);
        setError('Failed to fetch trails. Please try again later.'); // Set error message
      }
    };

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(position => {
        const { latitude, longitude } = position.coords;
        fetch(`https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${latitude}&longitude=${longitude}&localityLanguage=en`)
          .then(response => response.json())
          .then(data => {
            const city = data.city || data.locality || data.principalSubdivision || "Your Location";
            setUserLocation(city);
            fetchTrails(city); // Fetch trails for the detected city
          });
      }, () => {
        setCitySelectorOpen(true); // Open city selector if location access is denied
      });
    } else {
      setCitySelectorOpen(true); // Open city selector if geolocation is not supported
    }
  }, []);

  const handleCitySelect = async (city) => {
    setUserLocation(city);
    const response = await fetch(`http://localhost:8000/api/trails?city=${city}`); // Use port 8000
    const trails = await response.json();
    setFilteredTrails(trails);
    setCitySelectorOpen(false); // Close the city selector
  };

  const displayedTrails = showAll ? filteredTrails : filteredTrails.slice(0, 4);

  return (
    <div className="home-container">
      <div className="hero-section" style={{ backgroundImage: `url(http://localhost:8000/images/mountains.jpg)` }}>
        <div className="hero-content">
          <h1>Discover Your Next Adventure</h1>
          <p>Join our community of hikers and find the perfect trail</p>

          <div className="search-container">
            <input
              type="text"
              placeholder="Search for trails..."
              className="search-input"
            />
          </div>
        </div>
      </div>

      <div className="trails-section">
        <h2>Popular Trails near {userLocation}</h2>
        {error && <p className="error-message">{error}</p>} {/* Display error message if exists */}

        <div className="trails-grid">
          {displayedTrails.length > 0 ? (
            displayedTrails.map(trail => (
              <TrailCard key={trail._id} trail={trail} />
            ))
          ) : (
            <p>No trails found in your area.</p>
          )}
        </div>

        {filteredTrails.length > 4 && (
          <div className="show-more-container">
            <Button
              variant="outline"
              onClick={() => setShowAll(!showAll)}
              className="show-more-button"
            >
              {showAll ? 'Show Less' : 'Show More'}
            </Button>
          </div>
        )}
      </div>

      {/* City Selector Modal */}
      {citySelectorOpen && (
        <div className="modal-overlay">
          <CitySelector
            cities={Object.keys(locationMapping)} // Pass the list of cities
            onSelectCity={handleCitySelect}
            onClose={() => setCitySelectorOpen(false)}
          />
        </div>
      )}
    </div>
  );
};

export default Home;
