import React, { useState, useEffect, useRef } from 'react';
import { Mountain } from 'lucide-react';

const Navbar = ({ isLoggedIn, setIsLoggedIn }) => {
  const [dropdownVisible, setDropdownVisible] = useState(false);
  const dropdownRef = useRef(null);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    setDropdownVisible(false);
  };

  const toggleDropdown = (e) => {
    e.stopPropagation();
    setDropdownVisible(prev => !prev);
  };

  const handleClickOutside = (e) => {
    if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
      setDropdownVisible(false);
    }
  };

  useEffect(() => {
    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, []);

  return (
    <nav className="bg-gradient-to-r from-blue-600 to-blue-800 shadow-lg">
      <div className="max-w-6xl mx-auto px-4 py-3">
        <div className="flex justify-between items-center">
          {/* Logo Section */}
          <a href="/" className="flex items-center space-x-2 text-white hover:opacity-90 transition-opacity">
            <Mountain size={24} className="text-white" />
            <span className="text-xl font-bold">TrailMates</span>
          </a>

          {/* Navigation Links */}
          <div className="flex items-center space-x-8">
            <a href="/" className="text-white hover:text-blue-200 transition-colors">Home</a>
            <a href="/community" className="text-white hover:text-blue-200 transition-colors">Community</a>
            <a href="/shop" className="text-white hover:text-blue-200 transition-colors">Shop</a>

            {isLoggedIn ? (
              <div className="relative" ref={dropdownRef}>
                <button
                  onClick={toggleDropdown}
                  className="flex items-center focus:outline-none"
                >
                  <img
                    src="/api/placeholder/40/40"
                    alt="Profile"
                    className="w-10 h-10 rounded-full border-2 border-white hover:border-blue-200 transition-colors"
                  />
                </button>

                {dropdownVisible && (
                  <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-xl border border-gray-100 py-1 z-50">
                    <div className="px-4 py-2 border-b border-gray-100">
                      <p className="text-sm font-semibold text-gray-700">User's Name</p>
                      <p className="text-xs text-gray-500">user@email.com</p>
                    </div>
                    <a href="/profile" className="block px-4 py-2 text-sm text-gray-700 hover:bg-blue-50">Profile</a>
                    <a href="/settings" className="block px-4 py-2 text-sm text-gray-700 hover:bg-blue-50">Settings</a>
                    <button
                      onClick={handleLogout}
                      className="w-full text-left px-4 py-2 text-sm text-red-600 hover:bg-red-50"
                    >
                      Logout
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <a
                href="/login"
                className="bg-white text-blue-600 px-4 py-2 rounded-lg font-medium hover:bg-blue-50 transition-colors"
              >
                Login
              </a>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
