import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './cartpage.css';

const CartPage = () => {
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [itemCount, setItemCount] = useState(0);

  useEffect(() => {
    const items = JSON.parse(localStorage.getItem('cart')) || [];
    setCartItems(items);
    calculateTotal(items);
    setItemCount(items.reduce((acc, item) => acc + item.quantity, 0));
  }, []);

  const calculateTotal = (items) => {
    const subtotal = items.reduce((acc, item) => acc + item.price * item.quantity, 0);
    setTotalPrice(subtotal);
  };

  const updateProductStock = async (productId, quantityChange) => {
    try {
      await fetch(`http://localhost:8000/api/products/${productId}/update-stock`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ quantityChange }),
      });
    } catch (error) {
      console.error('Error updating product stock:', error);
    }
  };

  const handleCheckout = () => {
    navigate('/payment');
  };


  const handleRemoveItem = (itemId, itemQuantity) => {
    const updatedCart = cartItems.filter(item => item.id !== itemId);
    setCartItems(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
    calculateTotal(updatedCart);
    setItemCount(updatedCart.reduce((acc, item) => acc + item.quantity, 0));

    // Add the removed quantity back to the stock
    updateProductStock(itemId, itemQuantity);
  };

  const handleUpdateQuantity = (itemId, amount) => {
    const updatedCart = cartItems.map(item => {
      if (item.id === itemId) {
        const newQuantity = Math.max(1, item.quantity + amount);

        // Update the stock in the backend accordingly
        const quantityChange = amount > 0 ? -amount : Math.abs(amount);
        updateProductStock(itemId, amount > 0 ? -1 : 1);

        return { ...item, quantity: newQuantity };
      }
      return item;
    });

    setCartItems(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
    calculateTotal(updatedCart);
    setItemCount(updatedCart.reduce((acc, item) => acc + item.quantity, 0));
  };

  const handleContinueShopping = () => {
    navigate('/products'); // Navigate to shop page
  };

  return (
    <div className="cart-page">
      <div className="cart-container">
        <div className="cart-header">
          <h1 className="cart-title">Shopping Cart</h1>
          <button className="continue-shopping" onClick={handleContinueShopping}>
            Continue Shopping
          </button>
        </div>

        {cartItems.length === 0 ? (
          <div className="empty-cart">
            <p>Your cart is empty</p>
            <button className="continue-shopping" onClick={handleContinueShopping}>
              Continue Shopping
            </button>
          </div>
        ) : (
          <>
            <div className="cart-items">
              {cartItems.map(item => (
                <div key={item.id} className="cart-item">
                  <div className="item-image">
                  <img src={`http://localhost:8000${item.image}`} alt={item.name} />
                  </div>

                  <div className="item-details">
                    <h2>{item.name}</h2>
                    <div className="item-price-mobile">
                    ₹{item.price.toFixed(2)}
                    </div>

                    <div className="quantity-controls">
                      <button 
                        className="quantity-btn"
                        onClick={() => handleUpdateQuantity(item.id, -1)}
                        disabled={item.quantity <= 1}
                      >
                        −
                      </button>
                      <span className="quantity">{item.quantity}</span>
                      <button 
                        className="quantity-btn"
                        onClick={() => handleUpdateQuantity(item.id, 1)}
                      >
                        +
                      </button>
                    </div>

                    <button 
                      className="remove-btn"
                      onClick={() => handleRemoveItem(item.id, item.quantity)}
                    >
                      Remove
                    </button>
                  </div>

                  <div className="item-price">
                  ₹{item.price.toFixed(2)}
                  </div>
                </div>
              ))}
            </div>

            <div className="cart-footer">
              <div className="cart-summary">
                <div className="summary-details">
                  <div className="summary-row">
                    <span>Subtotal ({itemCount} items)</span>
                    <span>₹{totalPrice.toFixed(2)}</span>
                  </div>
                  <div className="summary-row">
                    <span>Shipping</span>
                    <span>Free</span>
                  </div>
                  <div className="summary-total">
                    <span>Total</span>
                    <span>₹{totalPrice.toFixed(2)}</span>
                  </div>
                </div>
                <button className="checkout-btn" onClick={handleCheckout}>
  Proceed to Checkout
</button>              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default CartPage;
