import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

const Navbar = ({ isLoggedIn, setIsLoggedIn }) => {
  const [dropdownVisible, setDropdownVisible] = useState(false);
  const dropdownRef = useRef(null);  // Reference to the dropdown container

  // Handle logout action
  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false); // Update state to reflect logout
    setDropdownVisible(false); // Hide dropdown on logout
  };

  // Toggle dropdown visibility
  const toggleDropdown = () => {
    setDropdownVisible(prev => !prev);
  };

  // Close dropdown if clicked outside of profile container
  const handleClickOutside = (e) => {
    if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
      setDropdownVisible(false); // Close the dropdown if clicked outside
    }
  };

  // Add event listener for click outside
  useEffect(() => {
    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, []);

  return (
    <nav className="navbar">
      <div className="container navbar-container">
        <Link to="/" className="navbar-logo">
          <i className="fas fa-mountain"></i>
          TrailMates
        </Link>
        <div className="navbar-links">
          <Link to="/" className="navbar-link">Home</Link>
          <Link to="/community" className="navbar-link">Community</Link>
          <Link to="/shop" className="navbar-link">Shop</Link>
          {isLoggedIn ? (
            <div className="profile-container">
              <img
                src="https://via.placeholder.com/40"
                alt="Profile"
                className="profile-pic"
                onClick={toggleDropdown}  // Toggle dropdown visibility
              />
              {dropdownVisible && (
                <div className="dropdown" ref={dropdownRef}>
                  <p>User's Name</p>
                  <button onClick={handleLogout}>Logout</button>
                </div>
              )}
            </div>
          ) : (
            <Link to="/login" className="navbar-link">Login</Link>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
