import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userName, setUserName] = useState('');
  const [userProfilePic, setUserProfilePic] = useState('');

  useEffect(() => {
    // Check if the user is logged in by checking the token
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
      setUserName(localStorage.getItem('userName')); // Get user name from localStorage
      setUserProfilePic(localStorage.getItem('userProfilePic') || 'defaultProfilePicURL'); // Default profile pic if not set
    }
  }, []);

  const handleLogout = () => {
    // Clear localStorage and log out the user
    localStorage.removeItem('token');
    localStorage.removeItem('userName');
    localStorage.removeItem('userProfilePic');
    setIsLoggedIn(false);
    setUserName('');
    setUserProfilePic('');
  };

  return (
    <nav className="navbar">
      <div className="container navbar-container">
        <Link to="/" className="navbar-logo">
          <i className="fas fa-mountain"></i> TrailMates
        </Link>
        <div className="navbar-links">
          <Link to="/" className="navbar-link">Home</Link>
          <Link to="/community" className="navbar-link">Community</Link>
          <Link to="/shop" className="navbar-link">Shop</Link>

          {isLoggedIn ? (
            <div className="profile-dropdown">
              <img
                src={userProfilePic}
                alt="Profile"
                className="profile-pic"
                onClick={() => document.getElementById('dropdown-menu').classList.toggle('show')}
              />
              <div id="dropdown-menu" className="dropdown-menu">
                <p>{userName}</p>
                <button onClick={handleLogout}>Logout</button>
              </div>
            </div>
          ) : (
            <Link to="/login" className="navbar-link">Login</Link>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
