import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import Navbar from './components/Navbar/Navbar';
import Home from './pages/Home/Home';
import Login from './pages/Login/Login';
import TrailDetails from './pages/TrailDetails/TrailDetails';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Check for token in localStorage to determine if the user is logged in
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true); // User is logged in, show homepage
      navigate('/'); // Ensure user is on homepage if logged in
    } else {
      navigate('/login'); // Redirect to login if no token
    }
  }, [navigate]);

  return (
    <div className="app">
      <Navbar isLoggedIn={isLoggedIn} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/trails/:id" element={<TrailDetails />} />
      </Routes>
    </div>
  );
}

export default App;
