// src/pages/TrailDetails/TrailDetails.js
import React from 'react';
import { useParams } from 'react-router-dom';
import { featuredTrails } from '../../data/data'; // Import the featuredTrails array

const TrailDetails = () => {
  const { id } = useParams();
  const trail = featuredTrails.find(trail => trail.id === parseInt(id));

  if (!trail) return <p>Trail not found</p>;

  return (
    <div>
      <h1>{trail.name}</h1>
      <img src={trail.image} alt={trail.name} />
      <p>Location: {trail.location}</p>
      <p>Difficulty: {trail.difficulty}</p>
      <p>Length: {trail.length}</p>
      <p>Elevation: {trail.elevation}</p>

      {/* Placeholder for customer reviews */}
      <section>
        <h2>Customer Reviews</h2>
        {/* Render customer reviews here */}
      </section>
    </div>
  );
};

export default TrailDetails;
