import React, { useState } from 'react';

const Community = () => {
  const [formData, setFormData] = useState({
    name: '',
    location: '',
    city: '',
    difficulty: '',
    length: '',
    elevation: '',
    images: []  // You can add an image upload functionality if needed
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8000/api/trails/submit-trail', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      if (response.ok) {
        alert('Trail submitted for approval');
        setFormData({
          name: '',
          location: '',
          city: '',
          difficulty: '',
          length: '',
          elevation: '',
          images: []
        });
      } else {
        console.error('Failed to submit trail');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="name" value={formData.name} onChange={handleChange} placeholder="Trail Name" required />
      <input name="location" value={formData.location} onChange={handleChange} placeholder="Location" required />
      <input name="city" value={formData.city} onChange={handleChange} placeholder="City" required />
      <input name="difficulty" value={formData.difficulty} onChange={handleChange} placeholder="Difficulty" required />
      <input name="length" value={formData.length} onChange={handleChange} placeholder="Length" required />
      <input name="elevation" value={formData.elevation} onChange={handleChange} placeholder="Elevation" required />
      {/* Add file upload for images if needed */}
      <button type="submit">Submit Trail</button>
    </form>
  );
};

export default Community;
