import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './TrailDetails.css';

const TrailDetails = () => {
  const { id } = useParams();
  const [trail, setTrail] = useState(null);
  const [currentImg, setCurrentImg] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showReview, setShowReview] = useState(false);
  const [reviewText, setReviewText] = useState('');
  const [rating, setRating] = useState(5);

  // ... keep other functions the same ...

  const deleteReview = async (reviewId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert("Log in to delete review.");
      return;
    }

    try {
      const res = await fetch(`http://localhost:8000/api/trails/${id}/reviews/${reviewId}`, {
        method: 'DELETE',
        headers: { 
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
      });

      if (!res.ok) {
        const errorData = await res.json();
        throw new Error(errorData.message || 'Review deletion failed');
      }

      const updatedTrail = await res.json();
      setTrail(updatedTrail);
    } catch (err) {
      alert(err.message || 'Error deleting review.');
    }
  };

  // ... keep other code the same ...

  return (
    <div className="trail-detail">
      {/* ... keep other JSX the same ... */}

      <section className="reviews">
        <h2>Reviews</h2>
        {trail.reviews && trail.reviews.length > 0 ? (
          trail.reviews.map((review) => (
            <div key={review._id} className="review">
              <p><strong>{review.user}</strong>: {review.text}</p>
              <p>Rating: {review.rating} / 5</p>
              {review.userId === localStorage.getItem('_id') && (
                <button 
                  onClick={() => deleteReview(review._id)} 
                  className="delete-btn"
                >
                  Delete Review
                </button>
              )}
            </div>
          ))
        ) : (
          <p>No reviews yet.</p>
        )}
      </section>

      {/* ... keep rest of JSX the same ... */}
    </div>
  );
};

export default TrailDetails;
