import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);  // Track authentication state
  const navigate = useNavigate();

  // Check for authentication state on component mount
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);  // Set authenticated to true if token exists
    } else {
      setIsAuthenticated(false);  // If no token, set as false
    }
  }, []);  // Empty array ensures this runs only once on mount

  const handleLogout = () => {
    localStorage.removeItem('token');  // Clear the token from localStorage
    setIsAuthenticated(false);  // Update the state to reflect the logout
    navigate('/login');  // Redirect to login page
  };

  return (
    <nav className="navbar">
      <div className="container navbar-container">
        <Link to="/" className="navbar-logo">
          <i className="fas fa-mountain"></i>
          TrailMates
        </Link>
        <div className="navbar-links">
          <Link to="/" className="navbar-link">Home</Link>
          <Link to="/community" className="navbar-link">Community</Link>
          <Link to="/shop" className="navbar-link">Shop</Link>

          {!isAuthenticated ? (
            <Link to="/login" className="navbar-link">Login</Link>  // Show Login if not authenticated
          ) : (
            <div className="profile-menu">
              <img
                src="https://via.placeholder.com/40"  // Profile picture placeholder
                alt="Profile"
                className="profile-pic"
              />
              <div className="profile-dropdown">
                <p>Username</p>  {/* Display username or any other information */}
                <button onClick={handleLogout}>Logout</button>  {/* Logout button */}
              </div>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
