// components/HikingBuddySignup.js
import React, { useState } from 'react';

const HikingBuddySignup = ({ userId }) => {
  const [city, setCity] = useState('');


  const handleSubmit = async (e) => {
    e.preventDefault();

    const buddyData = {
      userId,
      city,

    };

    const response = await fetch('http://localhost:8000/api/hiking-buddy/signup', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(buddyData),
    });

    const data = await response.json();
    console.log(data);  // Log response data (new buddy info)
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="City"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        required
      />

      <button type="submit">Sign Up as Hiking Buddy</button>
    </form>
  );
};

export default HikingBuddySignup;
