import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom'; // No need to import Router here
import Navbar from './components/Navbar/Navbar';
import Home from './pages/Home/Home';
import Login from './pages/Login/Login';
import TrailDetails from './pages/TrailDetails/TrailDetails'; // Import the TrailDetails component
import Shop from './pages/Shop/Shop';
import ProductDetails from './pages/Shop/ProductDetails';

import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userId] = useState('userIdExample');  // Example userId, replace with actual user data


  // Check for token in localStorage when the component mounts
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);  // User is logged in if token exists
    }
  }, []); // Empty dependency array ensures this runs only once after the initial render

  return (
    <div className="app">
      <Navbar isLoggedIn={isLoggedIn} setIsLoggedIn={setIsLoggedIn} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
        <Route path="/trails/:id" element={<TrailDetails />} /> {/* Trail details route */}
        <Route path="/products" element={<Shop />} />
        <Route path="/shop/:id" element={<ProductDetails />} />
        <Route path="/community">
            <CommunityPage userId={userId} />
          </Route>
      </Routes>
    </div>
  );
}

export default App;
