import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './TrailDetails.css';

const TrailDetails = () => {
  const { id } = useParams(); // This is the id from the URL
  const [trail, setTrail] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTrail = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/trails/${id}`);
        if (!response.ok) {
          throw new Error('Failed to fetch the trail');
        }
        const trailData = await response.json();
        setTrail(trailData);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchTrail();
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;
  if (!trail) return <p className="not-found">Trail not found</p>;

  const [currentIndex, setCurrentIndex] = useState(0);

  // Function to go to the previous image
  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? trail.images.length - 1 : prevIndex - 1));
  };

  // Function to go to the next image
  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex === trail.images.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <div className="trail-details">
      <h1 className="trail-title">{trail.name}</h1>

      <div className="carousel">
        <button className="arrow left-arrow" onClick={handlePrev}>&lt;</button>
        <img
          src={`http://localhost:8000${trail.images[currentIndex]}`} // Ensure correct path
          alt={`${trail.name} ${currentIndex + 1}`}
          className="carousel-image"
        />
        <button className="arrow right-arrow" onClick={handleNext}>&gt;</button>
      </div>

      <div className="trail-info">
        <p><strong>Location:</strong> {trail.location}</p>
        <p><strong>Difficulty:</strong> {trail.difficulty}</p>
        <p><strong>Length:</strong> {trail.length}</p>
        <p><strong>Elevation:</strong> {trail.elevation}</p>
      </div>

      <section className="reviews-section">
        <h2>Customer Reviews</h2>
        {trail.reviews.map((review) => (
          <div key={review._id} className="review">
            <p><strong>{review.user}</strong>: {review.comment}</p>
            <p>Rating: {review.rating} / 5</p>
          </div>
        ))}
      </section>
    </div>
  );
};

export default TrailDetails;
