// components/AvailableBuddies.js
import React, { useState, useEffect } from 'react';

const AvailableBuddies = () => {
  const [buddies, setBuddies] = useState([]);

  useEffect(() => {
    const fetchBuddies = async () => {
      const response = await fetch('http://localhost:8000/api/hiking-buddy/find-buddies');
      const data = await response.json();
      setBuddies(data);
    };

    fetchBuddies();
  }, []);

  return (
    <div>
      <h2>Available Hiking Buddies</h2>
      {buddies.length > 0 ? (
        buddies.map((buddy) => (
          <div key={buddy._id}>
            <p>{buddy.city}</p>
            <p>{buddy.availableFor.join(', ')}</p>
            <p>{buddy.interests}</p>
            {/* Add a chat button that links to a chat interface */}
            <button>Chat</button>
          </div>
        ))
      ) : (
        <p>No hiking buddies found. Sign up to be a buddy!</p>
      )}
    </div>
  );
};

export default AvailableBuddies;
