import React, { useState, useEffect } from 'react';
import './CartPage.css';

const CartPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  // Fetch cart items from localStorage
  useEffect(() => {
    const items = JSON.parse(localStorage.getItem('cart')) || [];
    console.log("Cart Items:", items); // Debugging cart items
    setCartItems(items);
    calculateTotal(items);
  }, []);

  // Calculate the total price of items in the cart
  const calculateTotal = (items) => {
    const total = items.reduce((acc, item) => acc + item.price * item.quantity, 0);
    setTotalPrice(total);
  };

  // Remove item from the cart
  const handleRemoveItem = (itemId) => {
    const updatedCart = cartItems.filter(item => item.id !== itemId);
    setCartItems(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
    calculateTotal(updatedCart);
  };

  // Update item quantity in the cart
  const handleUpdateQuantity = (itemId, amount) => {
    const updatedCart = cartItems.map(item => 
      item.id === itemId ? { ...item, quantity: item.quantity + amount } : item
    ).filter(item => item.quantity > 0); // Remove item if quantity becomes 0
    setCartItems(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
    calculateTotal(updatedCart);
  };

  return (
    <div className="cart-page">
      <div className="cart-container">
        <h1 className="cart-title">Shopping Cart</h1>

        {/* Conditional rendering based on cart items */}
        {cartItems.length === 0 ? (
          <p className="empty-cart-message">Your cart is empty.</p>
        ) : (
          <div className="cart-items">
            {cartItems.map(item => (
              <div key={item.id} className="cart-item">
                <img src={item.image} alt={item.name} className="cart-item-image" />
                <div className="cart-item-details">
                  <h2 className="cart-item-name">{item.name}</h2>
                  <p className="cart-item-price">${item.price}</p>
                  <span className="cart-item-quantity">Quantity: {item.quantity}</span>

                  {/* Update quantity */}
                  <div className="cart-item-quantity-controls">
                    <button onClick={() => handleUpdateQuantity(item.id, -1)}>-</button>
                    <button onClick={() => handleUpdateQuantity(item.id, 1)}>+</button>
                  </div>

                  {/* Remove item */}
                  <button className="remove-item-btn" onClick={() => handleRemoveItem(item.id)}>
                    Remove
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Show total price and checkout button if cart has items */}
        {cartItems.length > 0 && (
          <div className="cart-summary">
            <h3>Total: ${totalPrice.toFixed(2)}</h3>
            <button className="checkout-btn">Proceed to Checkout</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default CartPage;
