import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './ProductDetails.css';

const ProductDetails = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/products/${id}`);
        if (!response.ok) throw new Error('Failed to fetch product details');
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        console.error('Error fetching product:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchProduct();
  }, [id]);

  if (loading) {
    return <div className="loading-spinner">Loading...</div>;
  }

  if (!product) {
    return <div className="error-message">Product not found</div>;
  }

  const handleQuantityChange = (amount) => {
    const newQuantity = quantity + amount;
    if (newQuantity < 1) return;

    if (newQuantity > product.quantity) {
      alert(`Only ${product.quantity} items available in stock`);
      return;
    }

    setQuantity(newQuantity);
  };

  const handleAddToCart = async () => {
    if (quantity > product.quantity) {
      alert(`Only ${product.quantity} items available in stock`);
      return;
    }

    const cartItem = {
      id: product._id,
      name: product.name,
      price: product.price,
      quantity,
      image: product.image,
    };

    const existingCart = JSON.parse(localStorage.getItem('cart')) || [];
    const itemIndex = existingCart.findIndex(item => item.id === cartItem.id);

    if (itemIndex > -1) {
      const newQuantity = existingCart[itemIndex].quantity + quantity;
      if (newQuantity <= product.quantity) {
        existingCart[itemIndex].quantity = newQuantity;
      } else {
        alert(`Only ${product.quantity} items available in stock.`);
        return;
      }
    } else {
      if (quantity <= product.quantity) {
        existingCart.push(cartItem);
      } else {
        alert(`Only ${product.quantity} items available in stock.`);
        return;
      }
    }

    localStorage.setItem('cart', JSON.stringify(existingCart));

    // Update stock in the backend
    try {
      await fetch(`http://localhost:8000/api/products/${id}/decrement`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ quantity }),
      });
    } catch (error) {
      console.error('Error updating product quantity:', error);
    }

    alert('Item added to cart!');
  };

  const handleBuyNow = async () => {
    const cartItem = {
      id: product._id,
      name: product.name,
      price: product.price,
      quantity,
      image: product.image,
    };

    const existingCart = JSON.parse(localStorage.getItem('cart')) || [];
    const itemIndex = existingCart.findIndex(item => item.id === cartItem.id);

    if (itemIndex > -1) {
      const newQuantity = existingCart[itemIndex].quantity + quantity;
      if (newQuantity <= product.quantity) {
        existingCart[itemIndex].quantity = newQuantity;
      } else {
        alert(`Only ${product.quantity} items available in stock.`);
        return;
      }
    } else {
      if (quantity <= product.quantity) {
        existingCart.push(cartItem);
      } else {
        alert(`Only ${product.quantity} items available in stock.`);
        return;
      }
    }

    localStorage.setItem('cart', JSON.stringify(existingCart));

    try {
      await fetch(`http://localhost:8000/api/products/${id}/decrement`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ quantity }),
      });
    } catch (error) {
      console.error('Error updating product quantity:', error);
    }

    navigate('/cart');
  };

  return (
    <div className="product-container">
      <div className="product-grid">
        <div className="product-image-section">
          <div className="image-wrapper">
            <img src={`http://localhost:8000${product.image}`} alt={product.name} />
          </div>
        </div>

        <div className="product-info-section">
          <h1 className="product-title">{product.name}</h1>
          <p className="product-description">{product.description}</p>

          <div className="quantity-selector">
            <button className="quantity-btn" onClick={() => handleQuantityChange(-1)}>
              -
            </button>
            <input type="text" className="quantity-input" value={quantity} readOnly />
            <button className="quantity-btn" onClick={() => handleQuantityChange(1)}>
              +
            </button>
          </div>

          <div className="product-actions">
            <button className="add-to-cart-btn" onClick={handleAddToCart}>
              Add to Cart
            </button>
            <button className="buy-now-btn" onClick={handleBuyNow}>
              Buy Now
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
