import React, { useState } from 'react';
import TrailCard from '../../components/TrailCard/TrailCard';
import heroBackground from '../../images/mountains.jpg';
import Button from '../../components/Button/Button';

import trail1Image from '../../images/trail1.jpg';
import trail2Image from '../../images/trail2.jpg';
import trail3Image from '../../images/trail3.jpg';
import trail4Image from '../../images/trail4.jpg';
import trail5Image from '../../images/trail5.jpg';
import './Home.css';

const Home = () => {
  const [showAll, setShowAll] = useState(false);

  const featuredTrails = [
    {
      id: 1,
      name: "Skandagiri Trek",
      difficulty: "Moderate",
      length: "5.2 miles",
      image: trail1Image,
      elevation: "4757 ft",
      location: "Chikkaballapur"
    },
    {
      id: 2,
      name: "Nandi Hills",
      difficulty: "Easy",
      length: "3.1 miles",
      image: trail2Image,
      elevation: "4851 ft",
      location: "Nandi Hills"
    },
    {
      id: 3,
      name: "Savandurga",
      difficulty: "Hard",
      length: "8.7 miles",
      image: trail3Image,
      elevation: "4022 ft",
      location: "Magadi"
    },
    {
      id: 4,
      name: "Makalidurga",
      difficulty: "Moderate",
      length: "6.2 miles",
      image: trail4Image,
      elevation: "4430 ft",
      location: "Doddaballapur"
    },
    {
      id: 5,
      name: "Kunti Betta",
      difficulty: "Easy",
      length: "4.3 miles",
      image: trail5Image,
      elevation: "2882 ft",
      location: "Mandya"
    },
  ]

  // Display only first 4 items if showAll is false
  const displayedTrails = showAll ? featuredTrails : featuredTrails.slice(0, 4);

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Hero Section */}
      <div className="relative h-96 bg-cover bg-center" style={{ backgroundImage: `url(${heroBackground})` }}>
        <div className="absolute inset-0 bg-black bg-opacity-50 flex flex-col items-center justify-center text-white">
          <h1 className="text-4xl font-bold mb-4">Discover Your Next Adventure</h1>
          <p className="text-xl">Join our community of hikers and find the perfect trail</p>
        </div>
      </div>

      {/* Trails Section */}
      <div className="max-w-7xl mx-auto px-4 py-12">
        <h2 className="text-3xl font-bold mb-8">Popular Trails in Bangalore</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {displayedTrails.map(trail => (
            <TrailCard key={trail.id} {...trail} />
          ))}
        </div>

        {/* Show More button */}
        {featuredTrails.length > 4 && (
          <div className="flex justify-center mt-8">
            <Button
              variant="outline"
              onClick={() => setShowAll(!showAll)}
            >
              {showAll ? 'Show Less' : 'Show More'}
            </Button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
