// src/components/CitySelector.js

import React, { useState } from 'react';
import './CitySelector.css'; // Ensure you create and import this CSS file

const CitySelector = ({ cities, onSelectCity, onClose }) => {
  const [searchTerm, setSearchTerm] = useState('');

  // Filter cities based on the search term
  const filteredCities = cities.filter(city =>
    city.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="city-selector">
      <h2>Select Your City</h2>
      <input
        type="text"
        placeholder="Search for a city..."
        className="city-search-input"
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
      />
      <ul className="city-list">
        {filteredCities.length > 0 ? (
          filteredCities.map((city, index) => (
            <li key={index} onClick={() => onSelectCity(city)} className="city-item">
              {city}
            </li>
          ))
        ) : (
          <li className="no-results">No cities found</li>
        )}
      </ul>
      <button className="close-button" onClick={onClose}>Close</button>
    </div>
  );
};

export default CitySelector;
