import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

const Navbar = ({ isLoggedIn, setIsLoggedIn }) => {
  const [dropdownVisible, setDropdownVisible] = useState(false);
  const dropdownRef = useRef(null);

  // Retrieve the user's full name from localStorage
  const fullName = localStorage.getItem('fullName'); // Assuming 'fullName' is saved in localStorage

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('fullName'); // Also remove full name on logout
    setIsLoggedIn(false);
    setDropdownVisible(false);
  };

  const toggleDropdown = (e) => {
    e.stopPropagation();
    setDropdownVisible(prev => !prev);
  };

  const handleClickOutside = (e) => {
    if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
      setDropdownVisible(false);
    }
  };

  useEffect(() => {
    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, []);

  return (
    <nav className="navbar">
      <div className="container navbar-container">
        <Link to="/" className="navbar-logo">
          <i className="fas fa-mountain"></i>
          TrailMates
        </Link>
        <div className="navbar-links">
          <Link to="/" className="navbar-link">Home</Link>
          <Link to="/community" className="navbar-link">Community</Link>
          <Link to="/shop" className="navbar-link">Shop</Link>
          {isLoggedIn ? (
            <div className="profile-container" ref={dropdownRef}>
              <button
                onClick={toggleDropdown}
                className="profile-button"
              >
                <img
                  src="https://via.placeholder.com/40"
                  alt="Profile"
                  className="profile-pic"
                />
              </button>

              {dropdownVisible && (
                <div className="dropdown">
                  <div className="dropdown-header">
                    <p className="user-name">{fullName || 'Users Name'}</p> {/* Display full name */}
                  </div>
                  <Link to="/profile" className="dropdown-item">Profile</Link>
                  <Link to="/settings" className="dropdown-item">Settings</Link>
                  <button
                    onClick={handleLogout}
                    className="dropdown-item logout-button"
                  >
                    Logout
                  </button>
                </div>
              )}
            </div>
          ) : (
            <Link to="/login" className="login-button">
              Login
            </Link>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
