// src/components/TrailCard/TrailCard.js
import React from 'react';
import './TrailCard.css';

const TrailCard = ({ trail = {} }) => {
  return (
    <div className="trail-card">
      <div className="trail-card-image">
        {trail.image ? (
          <img src={trail.image} alt={trail.name || "Trail"} />
        ) : (
          <p>No image available</p>
        )}
        {trail.difficulty && <div className="trail-difficulty">{trail.difficulty}</div>}
      </div>
      <div className="trail-card-content">
        <h3>{trail.name || "Unknown Trail"}</h3>
        <div className="trail-info">
          <p className="location">üìç {trail.location || "Location not specified"}</p>
          <p className="length">‚ö° {trail.length || "Length not specified"}</p>
          <p className="elevation">üèî {trail.elevation || "Elevation not specified"}</p>
        </div>
      </div>
    </div>
  );
};

export default TrailCard;
