// ProductDetails.jsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './ProductDetails.css';

const ProductDetails = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/products/${id}`);
        if (!response.ok) throw new Error('Failed to fetch product details');
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        console.error('Error fetching product:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchProduct();
  }, [id]);

  if (loading) {
    return <div className="loading-spinner">Loading...</div>;
  }

  if (!product) {
    return <div className="error-message">Product not found</div>;
  }

  // Format prices using the same formatting as ProductCard
  const formattedPrice = new Intl.NumberFormat('en-IN', {
    style: 'currency',
    currency: 'INR',
    maximumFractionDigits: 0,
  }).format(product.price);

  const formattedOriginalPrice = product.originalPrice 
    ? new Intl.NumberFormat('en-IN', {
        style: 'currency',
        currency: 'INR',
        maximumFractionDigits: 0,
      }).format(product.originalPrice)
    : null;

  const discountPercentage = product.originalPrice 
    ? Math.round(((product.originalPrice - product.price) / product.originalPrice) * 100)
    : 0;

  return (
    <div className="product-container">
      <div className="product-grid">
        {/* Image Section */}
        <div className="product-image-section">
          <div className="image-wrapper">
            <img 
              src={`http://localhost:8000${product.image}`} 
              alt={product.name} 
            />
            {product.isNewArrival && <span className="badge new">New</span>}
            {discountPercentage > 0 && (
              <span className="badge discount">{discountPercentage}% OFF</span>
            )}
          </div>
        </div>

        {/* Product Info Section */}
        <div className="product-info-section">
          {product.category && (
            <span className="product-category">{product.category}</span>
          )}

          <h1 className="product-title">{product.name}</h1>

          <div className="product-price-container">
            <span className="current-price">{formattedPrice}</span>
            {formattedOriginalPrice && (
              <span className="original-price">{formattedOriginalPrice}</span>
            )}
            {discountPercentage > 0 && (
              <span className="discount-tag">{discountPercentage}% off</span>
            )}
          </div>

          {product.stockStatus && (
            <div className={`stock-status ${product.stockStatus.toLowerCase()}`}>
              {product.stockStatus}
            </div>
          )}

          <p className="product-description">{product.description}</p>

          <div className="product-actions">
            <button className="buy-now-btn">
              Buy Now
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
