import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import CitySelector from '../../components/CitySelector/Cityselector';  // Import the CitySelector

const Navbar = ({ loggedIn, setLoggedIn }) => {
  const [dropdown, setDropdown] = useState(false);
  const [cityModal, setCityModal] = useState(false); // State for controlling city modal
  const dropdownRef = useRef(null);

  const username = localStorage.getItem('name');
  const savedCity = localStorage.getItem('selectedCity'); // Get the selected city from localStorage

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('name');
    localStorage.removeItem('selectedCity'); // Optionally clear city as well on logout
    setLoggedIn(false);
    setDropdown(false);
  };

  const toggleDropdown = (e) => {
    e.stopPropagation();
    setDropdown(prev => !prev);
  };

  const closeDropdown = (e) => {
    if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
      setDropdown(false);
    }
  };

  const handleCityChange = (city) => {
    localStorage.setItem('selectedCity', city); // Save the selected city to localStorage
    setCityModal(false); // Close the city selector modal
  };

  useEffect(() => {
    document.addEventListener('click', closeDropdown);
    return () => {
      document.removeEventListener('click', closeDropdown);
    };
  }, []);

  return (
    <nav className="navbar">
      <div className="container">
        <Link to="/" className="logo">
          <i className="fas fa-mountain"></i> TrailMates
        </Link>

        {/* Display the selected city text next to the logo */}
        {savedCity && (
          <div className="selected-city" onClick={() => setCityModal(true)}>
            {savedCity} <span className="change-city">(Change)</span>
          </div>
        )}

        <div className="links">
          <Link to="/" className="link">Home</Link>
          <Link to="/community" className="link">Community</Link>
          <Link to="/products" className="link">Shop</Link>
          <Link to="/about" className="link">About Us</Link> {/* Added About Us link */}
          {loggedIn ? (
            <div className="profile" ref={dropdownRef}>
              <button onClick={toggleDropdown} className="profile-btn">
                <img
                  src="https://w7.pngwing.com/pngs/525/520/png-transparent-avatar-boy-people-person-profile-tom-user-user-avatar-icon.png"
                  alt="Profile"
                  className="profile-pic"
                />
              </button>
              {dropdown && (
                <div className="dropdown">
                  <div className="header">
                    <p className="name">{username || 'User'}</p>
                  </div>
                  <Link to="/profile" className="item">Profile</Link>
                  <Link to="/settings" className="item">Settings</Link>
                  <button onClick={logout} className="item logout">Logout</button>
                </div>
              )}
            </div>
          ) : (
            <Link to="/login" className="login-btn">Login</Link>
          )}
        </div>
      </div>

      {/* CitySelector Modal */}
      {cityModal && (
        <div className="modal-overlay">
          <CitySelector
            cities={Object.keys(locationMapping)}
            onSelectCity={handleCityChange}
            onClose={() => setCityModal(false)}
          />
        </div>
      )}
    </nav>
  );
};

export default Navbar;
