import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './TrailDetails.css';

const TrailDetails = () => {
  const { id } = useParams();
  const [trail, setTrail] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showAddReview, setShowAddReview] = useState(false);
  const [reviewText, setReviewText] = useState('');
  const [reviewRating, setReviewRating] = useState(5);

  useEffect(() => {
    const fetchTrail = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/trails/${id}`);
        if (!response.ok) {
          throw new Error('Failed to fetch the trail');
        }
        const trailData = await response.json();
        setTrail(trailData);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchTrail();
  }, [id]);

  const handlePrev = () => {
    if (trail) {
      setCurrentIndex((prevIndex) => (prevIndex === 0 ? trail.images.length - 1 : prevIndex - 1));
    }
  };

  const handleNext = () => {
    if (trail) {
      setCurrentIndex((prevIndex) => (prevIndex === trail.images.length - 1 ? 0 : prevIndex + 1));
    }
  };

  const handleAddReviewClick = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert("Please log in to add a review.");
    } else {
      setShowAddReview(true);
    }
  };

  const handleSubmitReview = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      alert("Please log in to add a review.");
      return;
    }

    const newReview = {
      user: localStorage.getItem('name'),
      comment: reviewText,
      rating: reviewRating,
    };

    try {
      const response = await fetch(`http://localhost:8000/api/trails/${id}/reviews`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify(newReview),
      });

      if (!response.ok) {
        throw new Error('Failed to submit review');
      }

      const updatedTrail = await response.json();
      setTrail(updatedTrail); // Update the trail data with the new review
      setShowAddReview(false);
      setReviewText('');
      setReviewRating(5);
    } catch (err) {
      alert('Could not submit review. Please try again.');
    }
  };

  const handleDeleteReview = async (reviewId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert("Please log in to delete a review.");
      return;
    }

    try {
      const response = await fetch(`http://localhost:8000/api/trails/${id}/reviews/${reviewId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error('Failed to delete review');
      }

      const updatedTrail = await response.json();
      setTrail(updatedTrail); // Update the trail data after review deletion
    } catch (err) {
      alert('Could not delete review. Please try again.');
    }
  };


  if (loading) return <p>Loading trail details...</p>;
  if (error) return <p className="error">{error}</p>;
  if (!trail) return <p className="not-found">Trail not found</p>;

  return (
    <div className="trail-details">
      <h1 className="trail-title">{trail.name}</h1>

      <div className="carousel">
        <button className="arrow left-arrow" onClick={handlePrev}>&lt;</button>
        <img
          src={`http://localhost:8000${trail.images[currentIndex]}`}
          alt={`${trail.name} ${currentIndex + 1}`}
          className="carousel-image"
        />
        <button className="arrow right-arrow" onClick={handleNext}>&gt;</button>
      </div>

      <div className="trail-info">
        <p><strong>Location:</strong> {trail.location}</p>
        <p><strong>Difficulty:</strong> {trail.difficulty}</p>
        <p><strong>Length:</strong> {trail.length}</p>
        <p><strong>Elevation:</strong> {trail.elevation}</p>
      </div>

      <section className="reviews-section">
        <h2>Customer Reviews</h2>
        {trail.reviews && trail.reviews.length > 0 ? (
          trail.reviews.map((review) => (
            <div key={review._id} className="review">
              <p><strong>{review.user}</strong>: {review.comment}</p>
              <p>Rating: {review.rating} / 5</p>
              {review.user === localStorage.getItem('name') && (
                <button onClick={() => handleDeleteReview(review._id)} className="delete-review-button">
                  Delete Review
                </button>
              )}
            </div>
          ))
        ) : (
          <p>No reviews yet.</p>
        )}
      </section>

      <button onClick={handleAddReviewClick} className="add-review-button">
        Add Review
      </button>

      {showAddReview && (
        <div className="add-review-form">
          <h3>Write Your Review</h3>
          <form onSubmit={handleSubmitReview}>
            <label>
              Rating:
              <input
                type="number"
                min="1"
                max="5"
                value={reviewRating}
                onChange={(e) => setReviewRating(e.target.value)}
                required
              />
            </label>
            <label>
              Review:
              <textarea
                value={reviewText}
                onChange={(e) => setReviewText(e.target.value)}
                required
              />
            </label>
            <button type="submit">Submit Review</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default TrailDetails;
