// src/data/data.js

import trail1Image from '../images/trail1.jpg';
import trail2Image from '../images/mountains.jpg';
import trail3Image from '../images/trail3.jpg';
import trail4Image from '../images/trail4.jpg';
import trail5Image from '../images/trail5.jpg';

export const featuredTrails = [
  {
    id: 1,
    name: "Skandagiri Trek",
    difficulty: "Moderate",
    length: "5.2 miles",
    images: [trail1Image, trail2Image, trail1Image], // Use an array for consistency
    elevation: "4757 ft",
    location: "Chikkaballapur",
    reviews: [
      { id: 1, user: "Amit Sharma", rating: 4, comment: "Beautiful trek with amazing sunrise views!" },
      { id: 2, user: "Priya Desai", rating: 5, comment: "An unforgettable experience!" }
    ]
  },
  {
    id: 2,
    name: "Nandi Hills",
    difficulty: "Easy",
    length: "3.1 miles",
    images: [trail2Image, trail2Image, trail2Image],
    elevation: "4851 ft",
    location: "Nandi Hills",
    reviews: [
      { id: 1, user: "Rahul Patel", rating: 4, comment: "Perfect for a weekend getaway." },
      { id: 2, user: "Sneha Kapoor", rating: 3, comment: "Nice spot, but it gets crowded." }
    ]
  },
  {
    id: 3,
    name: "Savandurga",
    difficulty: "Hard",
    length: "8.7 miles",
    images: [trail3Image, trail3Image, trail3Image],
    elevation: "4022 ft",
    location: "Magadi",
    reviews: [
      { id: 1, user: "Karthik Nair", rating: 5, comment: "Challenging but the view from the top is unbeatable! Definitely not for beginners." },
      { id: 2, user: "Ayesha Khan", rating: 4, comment: "Intense climb, but it's a great workout. Bring lots of water!" }
    ]
  },
  {
    id: 4,
    name: "Makalidurga",
    difficulty: "Moderate",
    length: "6.2 miles",
    images: [trail4Image, trail4Image, trail4Image],
    elevation: "4430 ft",
    location: "Doddaballapur",
    reviews: [
      { id: 1, user: "Deepak Verma", rating: 4, comment: "Nice trek, good for a day hike. The views of the lake are wonderful." },
      { id: 2, user: "Anjali Mehta", rating: 5, comment: "Lovely trek, especially during monsoon season! The greenery is refreshing." }
    ]
  },
  {
    id: 5,
    name: "Kunti Betta",
    difficulty: "Easy",
    length: "4.3 miles",
    images: [trail5Image, trail5Image, trail5Image],
    elevation: "2882 ft",
    location: "Mandya",
    reviews: [
      { id: 1, user: "Manoj Jain", rating: 3, comment: "Good beginner trek, but could use better trail markings." },
      { id: 2, user: "Ritika Chawla", rating: 4, comment: "Fun and easy trek! Great spot for a day trip with friends." }
    ]
  }
];
