import React, { useState, useEffect } from 'react';
import TrailCard from '../../components/TrailCard/TrailCard';
import Button from '../../components/Button/button';
import CitySelector from '../../components/CitySelector/Cityselector';
import './Home.css';
import { locationMapping } from '../../data/location_mapping';

const Home = () => {
  const [showAll, setShowAll] = useState(false);
  const [location, setLocation] = useState('');
  const [trails, setTrails] = useState([]);
  const [cityModal, setCityModal] = useState(false);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  // Function to fetch trails based on city
  const fetchTrails = async (city) => {
    console.log(`fetchTrails called for city: ${city}`); // Debugging line
    try {
      const response = await fetch(`http://localhost:8000/api/trails?city=${city}`);
      if (!response.ok) throw new Error('Network response was not ok');
      const trails = await response.json();
      const approvedTrails = trails.filter(trail => trail.approved === true);
      console.log('Fetched trails:', trails); // Log the fetched trails
      console.log('Approved trails:', approvedTrails); // Log the approved trails
      setTrails(approvedTrails); // Update the trails state
    } catch (error) {
      console.error('Error fetching trails:', error);
      setError('Failed to fetch trails. Please try again later.');
    }
  };

  // Effect to check saved city in localStorage and fetch trails initially
  useEffect(() => {
    const savedCity = localStorage.getItem('selectedCity');
    console.log(`Saved city from localStorage: ${savedCity}`); // Debug log
    if (savedCity) {
      setLocation(savedCity); // Set location from localStorage
    } else {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(position => {
          const { latitude, longitude } = position.coords;
          fetch(`https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${latitude}&longitude=${longitude}&localityLanguage=en`)
            .then(response => response.json())
            .then(data => {
              const city = data.city || data.locality || data.principalSubdivision || "Your Location";
              console.log('City detected from geolocation:', city); // Debug log
              setLocation(city);
            });
        }, () => {
          setCityModal(true);
        });
      } else {
        setCityModal(true);
      }
    }
  }, []); // This effect should only run once, on initial load

  // Effect to re-fetch trails when location changes
  useEffect(() => {
    if (location) {
      console.log(`Location changed to: ${location}`); // Debugging log
      fetchTrails(location); // Re-fetch trails when location changes
    }
  }, [location]); // This effect should run every time `location` changes

  // Handler for city change - set new city and update state
  const handleCityChange = (city) => {
    console.log('Changing city to:', city); // Debug log
    setLocation(city); // Update location state
    localStorage.setItem('selectedCity', city); // Store city in localStorage
    setTrails([]); // Clear trails while new ones are fetched
    setCityModal(false); // Close the city selector modal

    // Force a re-fetch of the trails after state update
    setTimeout(() => {
      fetchTrails(city); // Fetch trails for the new city
    }, 100); // Increase timeout slightly to give state time to update
  };

  // Filter and display the trails based on search term and showAll
  const displayedTrails = trails
    .filter(trail => trail.name.toLowerCase().includes(searchTerm.toLowerCase()))
    .slice(0, showAll ? undefined : 4);

  return (
    <div className="home-container">
      <div className="main-banner" style={{ backgroundImage: `url(http://localhost:8000/images/search2.jpg)` }}>
        <div className="banner-content">
          <h1>Find Your Next Adventure</h1>
          <p>Explore new trails and join our community of hikers</p>

          <div className="search-box">
            <input
              type="text"
              placeholder="Search for trails..."
              className="search-input"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>
      </div>

      <div className="trail-list">
        <h2>Trails in {location}</h2>
        {error && <p className="error-msg">{error}</p>}

        <div className="trail-grid">
          {displayedTrails.length > 0 ? (
            displayedTrails.map(trail => (
              <TrailCard key={trail._id} trail={trail} />
            ))
          ) : (
            <p>No trails found in your area.</p>
          )}
        </div>

        {trails.length > 4 && (
          <div className="load-more">
            <Button
              variant="outline"
              onClick={() => setShowAll(!showAll)}
              className="load-more-button"
            >
              {showAll ? 'Show Less' : 'Show More'}
            </Button>
          </div>
        )}
      </div>

      {cityModal && (
        <div className="modal-overlay">
          <CitySelector
            cities={Object.keys(locationMapping)}
            onSelectCity={handleCityChange}
            onClose={() => setCityModal(false)}
          />
        </div>
      )}
    </div>
  );
};

export default Home;
