import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = ({ setIsLoggedIn }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ email: '', password: '', name: '' });
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccessMessage('');

    // Determine the URL for login or signup
    const url = isLogin ? '/api/auth/login' : '/api/auth/signup';

    // Send only the email and password for login, exclude name for login
    const requestData = isLogin
      ? { email: formData.email, password: formData.password } // only email and password for login
      : formData; // for signup, send the full formData including name

    try {
      const response = await fetch(`http://localhost:8000${url}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(requestData), // use requestData here
      });

      const data = await response.json();

      if (response.ok) {
        if (isLogin) {
          // Store token and name in localStorage
          localStorage.setItem('token', data.token);
          localStorage.setItem('name', data.user.name); // Store the user's name
          setIsLoggedIn(true);
          setSuccessMessage('Login successful!');
          navigate('/');
        } else {
          setSuccessMessage('Account created successfully! Please log in.');
        }
      } else {
        setError(data.message || 'An error occurred. Please try again.');
      }
    } catch (err) {
      setError('Something went wrong. Please try again later.');
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div className="auth-page">
      <div className="login-container">
        <div className="auth-header">
          <h1>{isLogin ? 'Welcome Back' : 'Create Account'}</h1>
          <p className="auth-subtitle">
            {isLogin
              ? 'Enter your credentials to access your account'
              : 'Fill in the information to create your account'}
          </p>
        </div>

        <form onSubmit={handleSubmit} className="auth-form">
          {/* Show the 'name' input only when signing up */}
          {!isLogin && (
            <div className="form-group">
              <label htmlFor="name">Full Name</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                placeholder="Enter your full name"
                required
              />
            </div>
          )}

          {/* Email input */}
          <div className="form-group">
            <label htmlFor="email">Email Address</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter your email"
              required
            />
          </div>

          {/* Password input */}
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Enter your password"
              required
            />
          </div>

          {/* Display errors and success messages */}
          {error && <div className="alert error">{error}</div>}
          {successMessage && <div className="alert success">{successMessage}</div>}

          {/* Submit button */}
          <button type="submit" className="submit-button">
            {isLogin ? 'Sign In' : 'Create Account'}
          </button>
        </form>

        {/* Toggle between login and signup */}
        <div className="auth-footer">
          <button
            onClick={() => setIsLogin(!isLogin)}
            className="toggle-auth"
          >
            {isLogin
              ? "Don't have an account? Sign up"
              : 'Already have an account? Sign in'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
