import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

const Navbar = ({ isLoggedIn, setIsLoggedIn }) => {
  const [dropdownVisible, setDropdownVisible] = useState(false); // State to toggle dropdown visibility

  const handleLogout = () => {
    // Remove token from localStorage and update state
    localStorage.removeItem('token');
    setIsLoggedIn(false);  // Update the state to reflect that the user is logged out
    setDropdownVisible(false);  // Hide the dropdown when logging out
  };

  const toggleDropdown = () => {
    setDropdownVisible(!dropdownVisible);  // Toggle dropdown visibility
  };

  // Close the dropdown if clicked outside (useEffect in a real app for optimal solution)
  const handleClickOutside = (e) => {
    if (!e.target.closest('.profile-container')) {
      setDropdownVisible(false);  // Close dropdown if clicked outside
    }
  };

  React.useEffect(() => {
    document.addEventListener('click', handleClickOutside); // Listen for clicks outside
    return () => {
      document.removeEventListener('click', handleClickOutside); // Cleanup listener
    };
  }, []);

  return (
    <nav className="navbar">
      <div className="container navbar-container">
        <Link to="/" className="navbar-logo">
          <i className="fas fa-mountain"></i>
          TrailMates
        </Link>
        <div className="navbar-links">
          <Link to="/" className="navbar-link">Home</Link>
          <Link to="/community" className="navbar-link">Community</Link>
          <Link to="/shop" className="navbar-link">Shop</Link>
          {isLoggedIn ? (
            <div className="profile-container">
              <img 
                src="https://via.placeholder.com/40" 
                alt="Profile"
                className="profile-pic"
                onClick={toggleDropdown} // Toggle dropdown visibility when clicked
              />
              {dropdownVisible && (
                <div className="dropdown">
                  <p>User's Name</p>
                  <button onClick={handleLogout}>Logout</button>
                </div>
              )}
            </div>
          ) : (
            <Link to="/login" className="navbar-link">Login</Link>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
