// src/pages/Home/Home.js
import React from 'react';
import TrailCard from '../../components/TrailCard/TrailCard';
import heroBackground from '../../images/hero-bg.jpg'; // Add your image here
import './Home.css';

const Home = () => {
  const featuredTrails = [
    {
      id: 1,
      name: "Mountain Peak Trail",
      difficulty: "Moderate",
      length: "5.2 miles"
    },
    {
      id: 2,
      name: "Forest Loop",
      difficulty: "Easy",
      length: "3.1 miles"
    },
    {
      id: 3,
      name: "Valley Vista",
      difficulty: "Hard",
      length: "8.7 miles"
    },
    {

      id: 4,
      name: "Valley Vista",
      difficulty: "Hard",
      length: "8.7 miles"
    },
    {

      id: 5,
      name: "Valley Vista",
      difficulty: "Hard",
      length: "8.7 miles"
    },

  ];

  return (
    <main>
      <section className="hero" style={{ 
        background: `linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url(${heroBackground})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center'
      }}>
        <div className="container">
          <h1>Discover Your Next Adventure</h1>
          <p>Join our community of hikers and find the perfect trail</p>
          <div className="search-container">
            <input
              type="text"
              className="search-input"
              placeholder="Search for trails..."
            />
          </div>
        </div>
      </section>

      <section className="featured-trails container">
        <h2>Featured Trails</h2>
        <div className="trails-grid">
          {featuredTrails.map(trail => (
            <TrailCard key={trail.id} trail={trail} />
          ))}
        </div>
      </section>
    </main>
  );
};

export default Home;
