import React, { useState } from 'react';
import axios from 'axios';
import './SubmitTrailForm.css';

const SubmitTrailForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    location: '',
    city: '',
    difficulty: '',
    length: '',
    elevation: '',
    reviews: [],
  });

  const [images, setImages] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  // Handle input changes for text fields
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    // Clear any error messages when user starts typing
    setErrorMessage('');
  };

  // Handle image file uploads
  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    setImages(files); // Store the selected files as an array
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setErrorMessage('');
    setSuccessMessage('');

    // Ensure reviews is an array, even if empty
    const dataToSubmit = {
      ...formData,
      reviews: formData.reviews || [] // Ensure reviews is an empty array if not provided
    };

    try {
      const response = await axios.post('http://localhost:8000/api/submit', dataToSubmit);
      setSuccessMessage('Trail submitted successfully!');
      // Reset form after successful submission
      setFormData({
        name: '',
        location: '',
        city: '',
        difficulty: '',
        length: '',
        elevation: '',
        images: [],
        reviews: [],
      });
    } catch (error) {
      setErrorMessage(error.response?.data?.message || 'Error submitting trail. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
};


  return (
    <div className="trail-form-container">
      <h1>Submit a New Trail</h1>
      {successMessage && (
        <div className="success-message">
          {successMessage}
        </div>
      )}
      {errorMessage && (
        <div className="error-message">
          {errorMessage}
        </div>
      )}
      <form className="trail-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Trail Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            placeholder="Enter trail name"
          />
        </div>

        <div className="form-group">
          <label>Location:</label>
          <input
            type="text"
            name="location"
            value={formData.location}
            onChange={handleChange}
            required
            placeholder="Enter trail location"
          />
        </div>

        <div className="form-group">
          <label>City:</label>
          <input
            type="text"
            name="city"
            value={formData.city}
            onChange={handleChange}
            placeholder="Enter city name"
          />
        </div>

        <div className="form-group">
          <label>Difficulty:</label>
          <input
            type="text"
            name="difficulty"
            value={formData.difficulty}
            onChange={handleChange}
            required
            placeholder="Easy, Moderate, Hard, etc."
          />
        </div>

        <div className="form-group">
          <label>Length:</label>
          <input
            type="text"
            name="length"
            value={formData.length}
            onChange={handleChange}
            required
            placeholder="Enter trail length (e.g., 5.2 miles)"
          />
        </div>

        <div className="form-group">
          <label>Elevation:</label>
          <input
            type="text"
            name="elevation"
            value={formData.elevation}
            onChange={handleChange}
            required
            placeholder="Enter elevation gain (e.g., 1200 ft)"
          />
        </div>

        <div className="form-group">
          <label>Images:</label>
          <input
            type="file"
            name="image"
            onChange={handleImageChange}
            multiple
            accept="image/*"
          />
        </div>

        <div className="form-group">
          <button 
            className={`submit-button ${isSubmitting ? 'loading' : ''}`}
            type="submit"
            disabled={isSubmitting}
          >
            {isSubmitting ? 'Submitting...' : 'Submit Trail'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default SubmitTrailForm;
