import React, { useState } from 'react';
import axios from 'axios';

const SubmitTrailForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    location: '',
    city: '',
    difficulty: '',
    length: '',
    elevation: '',
    images: [],
    reviews: [],
  });

  // Handle input changes for text fields
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle image file uploads
  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    const fileNames = files.map(file => file.name); // You can change this if you need to upload to a cloud service
    setFormData({ ...formData, images: fileNames });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:8000/api/submit', formData);
      console.log(response.data);
      // Handle success (e.g., show a success message, redirect, etc.)
    } catch (error) {
      console.error("Error submitting trail:", error);
      // Handle error (e.g., show an error message)
    }
  };

  return (
    <div>
      <h1>Submit a New Trail</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Trail Name:</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </div>

        <div>
          <label>Location:</label>
          <input type="text" name="location" value={formData.location} onChange={handleChange} required />
        </div>

        <div>
          <label>City:</label>
          <input type="text" name="city" value={formData.city} onChange={handleChange} />
        </div>

        <div>
          <label>Difficulty:</label>
          <input type="text" name="difficulty" value={formData.difficulty} onChange={handleChange} required />
        </div>

        <div>
          <label>Length:</label>
          <input type="text" name="length" value={formData.length} onChange={handleChange} required />
        </div>

        <div>
          <label>Elevation:</label>
          <input type="text" name="elevation" value={formData.elevation} onChange={handleChange} required />
        </div>

        <div>
          <label>Images:</label>
          <input type="file" name="images" onChange={handleImageChange} multiple />
        </div>

        <div>
          <button type="submit">Submit Trail</button>
        </div>
      </form>
    </div>
  );
};

export default SubmitTrailForm;
