// src/pages/TrailDetails/TrailDetails.js
import React, { useRef } from 'react';
import { useParams } from 'react-router-dom';
import { featuredTrails } from '../../data/data';
import './TrailDetails.css';

const TrailDetails = () => {
  const { id } = useParams();
  const trail = featuredTrails.find(trail => trail.id === parseInt(id));

  const carouselRef = useRef(null); // Reference for the carousel

  if (!trail) return <p className="not-found">Trail not found</p>;

  const handleScroll = (e) => {
    // Prevent default scroll behavior
    e.preventDefault();
    // Scroll the carousel based on wheel movement
    carouselRef.current.scrollBy({
      left: e.deltaY, // Scroll amount based on wheel movement
      behavior: 'smooth' // Smooth scrolling effect
    });
  };

  return (
    <div className="trail-details">
      <h1 className="trail-title">{trail.name}</h1>

      <div
        className="carousel"
        onWheel={handleScroll} // Add wheel event handler
        ref={carouselRef}
      >
        <div className="carousel-track">
          {trail.images.map((image, index) => (
            <img
              key={index}
              src={image}
              alt={`${trail.name} ${index + 1}`}
              className="carousel-image"
            />
          ))}
        </div>
      </div>

      <div className="trail-info">
        <p><strong>Location:</strong> {trail.location}</p>
        <p><strong>Difficulty:</strong> {trail.difficulty}</p>
        <p><strong>Length:</strong> {trail.length}</p>
        <p><strong>Elevation:</strong> {trail.elevation}</p>
      </div>

      <section className="reviews-section">
        <h2>Customer Reviews</h2>
        {trail.reviews.map((review) => (
          <div key={review.id} className="review">
            <p><strong>{review.user}</strong>: {review.comment}</p>
            <p>Rating: {review.rating} / 5</p>
          </div>
        ))}
      </section>
    </div>
  );
};

export default TrailDetails;
