import React, { useState } from 'react';
import TrailCard from '../../components/TrailCard/TrailCard';
import Button from '../../components/Button/button';
import heroBackground from '../../images/mountains.jpg';
import trail1Image from '../../images/trail1.jpg';
import trail2Image from '../../images/trail2.jpg';
import trail3Image from '../../images/trail3.jpg';
import trail4Image from '../../images/trail4.jpg';
import trail5Image from '../../images/trail5.jpg';
import './Home.css';

const Home = () => {
  const [showAll, setShowAll] = useState(false);

  const featuredTrails = [
    {
      id: 1,
      name: "Skandagiri Trek",
      difficulty: "Moderate",
      length: "5.2 miles",
      image: trail1Image,
      elevation: "4757 ft",
      location: "Chikkaballapur"
    },
    {
      id: 2,
      name: "Nandi Hills",
      difficulty: "Easy",
      length: "3.1 miles",
      image: trail2Image,
      elevation: "4851 ft",
      location: "Nandi Hills"
    },
    {
      id: 3,
      name: "Savandurga",
      difficulty: "Hard",
      length: "8.7 miles",
      image: trail3Image,
      elevation: "4022 ft",
      location: "Magadi"
    },
    {
      id: 4,
      name: "Makalidurga",
      difficulty: "Moderate",
      length: "6.2 miles",
      image: trail4Image,
      elevation: "4430 ft",
      location: "Doddaballapur"
    },
    {
      id: 5,
      name: "Kunti Betta",
      difficulty: "Easy",
      length: "4.3 miles",
      image: trail5Image,
      elevation: "2882 ft",
      location: "Mandya"
    },
  ];

  const displayedTrails = showAll ? featuredTrails : featuredTrails.slice(0, 4);

  return (
    <div className="home-container">
      {/* Hero Section */}
      <div className="hero-section" style={{ backgroundImage: `url(${heroBackground})` }}>
        <div className="hero-content">
          <h1>Discover Your Next Adventure</h1>
          <p>Join our community of hikers and find the perfect trail</p>

          {/* Search Section */}
          <div className="search-container">
            <input
              type="text"
              placeholder="Search for trails..."
              className="search-input"
            />
          </div>
        </div>
      </div>

      {/* Trails Section */}
      <div className="trails-section">
        <h2>Popular Trails in Bangalore</h2>

        <div className="trails-grid">
          {displayedTrails.map(trail => (
            <TrailCard key={trail.id} trail={trail} />
          ))}
        </div>

        {/* Show More button */}
        {featuredTrails.length > 4 && (
          <div className="show-more-container">
            <Button
              variant="outline"
              onClick={() => setShowAll(!showAll)}
              className="show-more-button"
            >
              {showAll ? 'Show Less' : 'Show More'}
            </Button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
