import React, { useState } from 'react';
import axios from 'axios';
import './SubmitTrailForm.css';

const SubmitTrailForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    location: '',
    city: '',
    difficulty: '',
    length: '',
    elevation: '',
    images: [],
    reviews: [{ user: '', comment: '', rating: '' }]
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleImageChange = (e) => {
    setFormData({ ...formData, images: Array.from(e.target.files) });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/api/submit', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      if (response.ok) {
        alert(data.message);
      } else {
        alert(data.error || 'Error submitting trail');
      }
    } catch (error) {
      alert('Network error. Please try again later.');
    }
  };


  return (
    <div className="submit-trail-form">
      <h2>Submit a Trail</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Trail Name
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </label>
        <label>
          Location
          <input type="text" name="location" value={formData.location} onChange={handleChange} required />
        </label>
        <label>
          City
          <input type="text" name="city" value={formData.city} onChange={handleChange} required />
        </label>
        <label>
          Difficulty
          <select name="difficulty" value={formData.difficulty} onChange={handleChange} required>
            <option value="">Select difficulty</option>
            <option value="Easy">Easy</option>
            <option value="Moderate">Moderate</option>
            <option value="Hard">Hard</option>
          </select>
        </label>
        <label>
          Length (km)
          <input type="text" name="length" value={formData.length} onChange={handleChange} required />
        </label>
        <label>
          Elevation (m)
          <input type="text" name="elevation" value={formData.elevation} onChange={handleChange} required />
        </label>
        <label>
          Upload Images
          <input type="file" multiple onChange={handleImageChange} />
        </label>
        <button type="submit">Submit Trail</button>
      </form>
    </div>
  );
};

export default SubmitTrailForm;
