import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Track if the user is logged in
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // Track if the dropdown menu is open
  const navigate = useNavigate();

  // Check if the user is logged in on initial load
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true); // User is logged in
    }
  }, []);

  // Handle logout functionality
  const handleLogout = () => {
    localStorage.removeItem('token'); // Remove token from localStorage
    setIsLoggedIn(false); // Update logged in state
    navigate('/'); // Redirect to home page after logout
  };

  return (
    <nav className="navbar">
      <div className="container navbar-container">
        <Link to="/" className="navbar-logo">
          <i className="fas fa-mountain"></i>
          TrailMates
        </Link>
        <div className="navbar-links">
          <Link to="/" className="navbar-link">Home</Link>
          <Link to="/community" className="navbar-link">Community</Link>
          <Link to="/shop" className="navbar-link">Shop</Link>

          {!isLoggedIn ? (
            <Link to="/login" className="navbar-link">Login</Link> // Show Login button if not logged in
          ) : (
            <div className="navbar-profile">
              <img
                src="https://via.placeholder.com/40" // Placeholder image, you can replace it with actual user profile image
                alt="Profile"
                className="navbar-profile-img"
                onClick={() => setIsDropdownOpen(!isDropdownOpen)} // Toggle dropdown on profile pic click
              />
              {isDropdownOpen && (
                <div className="dropdown-menu">
                  <p className="dropdown-item">User Name</p> {/* You can replace 'User Name' with actual user name */}
                  <button className="dropdown-item" onClick={handleLogout}>Logout</button>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
