// src/pages/TrailDetails/TrailDetails.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './TrailDetails.css';

const TrailDetails = () => {
  const { id } = useParams();
  const [trail, setTrail] = useState(null); // State to hold the trail data
  const [currentIndex, setCurrentIndex] = useState(0); // State to track the current image index
  const [loading, setLoading] = useState(true); // State to track loading state
  const [error, setError] = useState(null); // State to track errors

  useEffect(() => {
    const fetchTrail = async () => {
      try {
        const response = await fetch(`http://localhost:8000/trails/${id}`); // Fetch trail by ID
        if (!response.ok) {
          throw new Error('Failed to fetch the trail');
        }
        const trailData = await response.json();
        setTrail(trailData);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchTrail();
  }, [id]);

  // Function to go to the previous image
  const handlePrev = () => {
    if (trail) {
      setCurrentIndex((prevIndex) => (prevIndex === 0 ? trail.images.length - 1 : prevIndex - 1));
    }
  };

  // Function to go to the next image
  const handleNext = () => {
    if (trail) {
      setCurrentIndex((prevIndex) => (prevIndex === trail.images.length - 1 ? 0 : prevIndex + 1));
    }
  };

  if (loading) return <p>Loading trail details...</p>;
  if (error) return <p className="error">{error}</p>;
  if (!trail) return <p className="not-found">Trail not found</p>;

  return (
    <div className="trail-details">
      <h1 className="trail-title">{trail.name}</h1>

      <div className="carousel">
        <button className="arrow left-arrow" onClick={handlePrev}>&lt;</button>
        <img
          src={`http://localhost:8000${trail.images[currentIndex]}`} // Adjust the image URL to match your backend
          alt={`${trail.name} ${currentIndex + 1}`}
          className="carousel-image"
        />
        <button className="arrow right-arrow" onClick={handleNext}>&gt;</button>
      </div>

      <div className="trail-info">
        <p><strong>Location:</strong> {trail.location}</p>
        <p><strong>Difficulty:</strong> {trail.difficulty}</p>
        <p><strong>Length:</strong> {trail.length}</p>
        <p><strong>Elevation:</strong> {trail.elevation}</p>
      </div>

      <section className="reviews-section">
        <h2>Customer Reviews</h2>
        {trail.reviews && trail.reviews.length > 0 ? (
          trail.reviews.map((review, index) => (
            <div key={index} className="review"> {/* Use index if review has no unique ID */}
              <p><strong>{review.user}</strong>: {review.comment}</p>
              <p>Rating: {review.rating} / 5</p>
            </div>
          ))
        ) : (
          <p>No reviews yet.</p>
        )}
      </section>
    </div>
  );
};

export default TrailDetails;
