// src/pages/TrailDetails/TrailDetails.js
import React, { useRef } from 'react';
import { useParams } from 'react-router-dom';
import { featuredTrails } from '../../data/data';
import './TrailDetails.css';

const TrailDetails = () => {
  const { id } = useParams();
  const trail = featuredTrails.find(trail => trail.id === parseInt(id));

  const carouselRef = useRef(null); // Reference for the carousel

  if (!trail) return <p className="not-found">Trail not found</p>;

  const handleDragStart = (e) => {
    const startX = e.pageX || e.touches[0].pageX; // Get the starting position
    const scrollLeft = carouselRef.current.scrollLeft; // Current scroll position
    const dragStart = startX - scrollLeft;

    const handleDragMove = (e) => {
      const x = e.pageX || e.touches[0].pageX; // Get the current position
      const walk = (x - dragStart); // Calculate how far the user has dragged
      carouselRef.current.scrollLeft = scrollLeft - walk; // Update the scroll position
    };

    const handleDragEnd = () => {
      document.removeEventListener('mousemove', handleDragMove); // Clean up
      document.removeEventListener('mouseup', handleDragEnd);
      document.removeEventListener('touchmove', handleDragMove); // Clean up for touch
      document.removeEventListener('touchend', handleDragEnd);
      carouselRef.current.style.cursor = 'grab'; // Reset cursor
    };

    document.addEventListener('mousemove', handleDragMove);
    document.addEventListener('mouseup', handleDragEnd);
    document.addEventListener('touchmove', handleDragMove); // For touch devices
    document.addEventListener('touchend', handleDragEnd);
    carouselRef.current.style.cursor = 'grabbing'; // Change cursor
  };

  return (
    <div className="trail-details">
      <h1 className="trail-title">{trail.name}</h1>

      <div className="carousel" onMouseDown={handleDragStart} onTouchStart={handleDragStart} ref={carouselRef}>
        <div className="carousel-track">
          {trail.images.map((image, index) => (
            <img
              key={index}
              src={image}
              alt={`${trail.name} ${index + 1}`}
              className="carousel-image"
            />
          ))}
        </div>
      </div>

      <div className="trail-info">
        <p><strong>Location:</strong> {trail.location}</p>
        <p><strong>Difficulty:</strong> {trail.difficulty}</p>
        <p><strong>Length:</strong> {trail.length}</p>
        <p><strong>Elevation:</strong> {trail.elevation}</p>
      </div>

      <section className="reviews-section">
        <h2>Customer Reviews</h2>
        {trail.reviews.map((review) => (
          <div key={review.id} className="review">
            <p><strong>{review.user}</strong>: {review.comment}</p>
            <p>Rating: {review.rating} / 5</p>
          </div>
        ))}
      </section>
    </div>
  );
};

export default TrailDetails;
