import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar/Navbar';
import Home from './pages/Home/Home';
import Login from './pages/Login/Login';
import TrailDetails from './pages/TrailDetails/TrailDetails';
import './App.css';

function App() {
  // State to track whether the user is logged in or not
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Check localStorage for token when the app loads
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);  // User is logged in
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token'); // Clear token on logout
    setIsLoggedIn(false);  // Update state
  };

  return (
    <Router>
      <div className="app">
        <Navbar isLoggedIn={isLoggedIn} onLogout={handleLogout} />
        <Routes>
          <Route path="/" element={isLoggedIn ? <Home /> : <Navigate to="/login" />} />  {/* Home page if logged in */}
          <Route path="/login" element={isLoggedIn ? <Navigate to="/" /> : <Login />} />  {/* Redirect to home if logged in */}
          <Route path="/trails/:id" element={<TrailDetails />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
