import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import Navbar from './components/Navbar/Navbar';
import Home from './pages/Home/Home';
import Login from './pages/Login/Login';
import TrailDetails from './pages/TrailDetails/TrailDetails';
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);  // Track authentication state
  const navigate = useNavigate();  // To redirect after login

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);  // If token exists, set authenticated to true
    } else {
      setIsAuthenticated(false);  // If no token, ensure user is logged out
      navigate('/login');  // Redirect to login page if not authenticated
    }
  }, [navigate]);

  return (
    <div className="app">
      <Navbar />
      <Routes>
        <Route path="/" element={<Home />} />  {/* Home route is the default */}
        {!isAuthenticated && <Route path="/login" element={<Login />} />} {/* Only show login if not authenticated */}
        <Route path="/trails/:id" element={<TrailDetails />} />
      </Routes>
    </div>
  );
}

export default App;
