import React, { useState, useEffect } from 'react';
import TrailCard from '../../components/TrailCard/TrailCard';
import Button from '../../components/Button/button';
import CitySelector from '../../components/CitySelector/Cityselector';
import './Home.css';
import { locationMapping } from '../../data/location_mapping';

const Home = () => {
  const [showAll, setShowAll] = useState(false);
  const [location, setLocation] = useState('');
  const [trails, setTrails] = useState([]);
  const [cityModal, setCityModal] = useState(false);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false); // Loading state to show during fetch

  // Function to fetch trails based on city
  const fetchTrails = async (city) => {
    console.log('Fetching trails for city:', city); // Debug log
    setLoading(true); // Set loading to true when fetch starts
    try {
      const response = await fetch(`http://localhost:8000/api/trails?city=${city}`);
      if (!response.ok) throw new Error('Network response was not ok');
      const trails = await response.json();
      console.log('Fetched trails:', trails); // Debug log to check what we are getting
      const approvedTrails = trails.filter(trail => trail.approved === true);
      console.log('Approved trails:', approvedTrails); // Debug log
      setTrails(approvedTrails); // Update state with new trails
    } catch (error) {
      setError('Failed to fetch trails. Please try again later.');
      console.error('Error fetching trails:', error); // Debug log
    } finally {
      setLoading(false); // Set loading to false when fetch completes
    }
  };

  useEffect(() => {
    // Retrieve city from localStorage if it exists
    const savedCity = localStorage.getItem('selectedCity');
    console.log('Saved city from localStorage:', savedCity); // Debug log

    if (savedCity) {
      console.log('Using saved city:', savedCity); // Debug log
      setLocation(savedCity); // Set saved city
      fetchTrails(savedCity); // Fetch trails for the saved city
    } else {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(position => {
          const { latitude, longitude } = position.coords;
          fetch(`https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${latitude}&longitude=${longitude}&localityLanguage=en`)
            .then(response => response.json())
            .then(data => {
              const city = data.city || data.locality || data.principalSubdivision || "Your Location";
              console.log('Using geo-location city:', city); // Debug log
              setLocation(city); // Set location based on geo
              fetchTrails(city); // Fetch trails for the geolocation-based city
            });
        }, () => {
          setCityModal(true);
        });
      } else {
        setCityModal(true);
      }
    }
  }, []); // Empty dependency array, runs once on initial load

  const handleCityChange = (city) => {
    console.log('City selected:', city); // Debug log
    setLocation(city); // Set the new location
    localStorage.setItem('selectedCity', city); // Save the selected city to localStorage

    // Force re-fetching trails when city changes
    setTrails([]); // Clear current trails state to show loading or empty state
    setLoading(true); // Set loading to true when new city is selected
    fetchTrails(city); // Re-fetch trails for the new city
    setCityModal(false); // Close the modal
  };

  const displayedTrails = trails
    .filter(trail => trail.name.toLowerCase().includes(searchTerm.toLowerCase()))
    .slice(0, showAll ? undefined : 4);

  return (
    <div className="home-container">
      <div className="main-banner" style={{ backgroundImage: `url(http://localhost:8000/images/search2.jpg)` }}>
        <div className="banner-content">
          <h1>Find Your Next Adventure</h1>
          <p>Explore new trails and join our community of hikers</p>

          <div className="search-box">
            <input
              type="text"
              placeholder="Search for trails..."
              className="search-input"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>
      </div>

      <div className="trail-list">
        <h2>Trails in {location}</h2>
        {error && <p className="error-msg">{error}</p>}
        {loading && <p>Loading trails...</p>} {/* Show loading text while fetching */}

        <div className="trail-grid">
          {displayedTrails.length > 0 ? (
            displayedTrails.map(trail => (
              <TrailCard key={trail._id} trail={trail} />
            ))
          ) : (
            <p>No trails found in your area.</p>
          )}
        </div>

        {trails.length > 4 && (
          <div className="load-more">
            <Button
              variant="outline"
              onClick={() => setShowAll(!showAll)}
              className="load-more-button"
            >
              {showAll ? 'Show Less' : 'Show More'}
            </Button>
          </div>
        )}
      </div>

      {cityModal && (
        <div className="modal-overlay">
          <CitySelector
            cities={Object.keys(locationMapping)}
            onSelectCity={handleCityChange}
            onClose={() => setCityModal(false)}
          />
        </div>
      )}
    </div>
  );
};

export default Home;
