const Navbar = ({ loggedIn, setLoggedIn }) => {
  const [dropdown, setDropdown] = useState(false);
  const dropdownRef = useRef(null);

  const username = localStorage.getItem('name'); // Ensure this is correctly set after login
  const profilePic = localStorage.getItem('profilePic') || 'https://w7.pngwing.com/pngs/525/520/png-transparent-avatar-boy-people-person-profile-tom-user-user-avatar-icon.png'; // Use localStorage for user profile pic

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('name');
    localStorage.removeItem('profilePic');
    setLoggedIn(false);
    setDropdown(false);
  };

  const toggleDropdown = (e) => {
    e.stopPropagation();
    setDropdown(prev => !prev);
  };

  const closeDropdown = (e) => {
    if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
      setDropdown(false);
    }
  };

  useEffect(() => {
    document.addEventListener('click', closeDropdown);
    return () => {
      document.removeEventListener('click', closeDropdown);
    };
  }, []);

  return (
    <nav className="navbar">
      <div className="container">
        <Link to="/" className="logo">
          <i className="fas fa-mountain"></i> TrailMates
        </Link>
        <div className="links">
          <Link to="/" className="link">Home</Link>
          <Link to="/community" className="link">Community</Link>
          <Link to="/products" className="link">Shop</Link>
          {loggedIn ? (
            <div className="profile" ref={dropdownRef}>
              <button onClick={toggleDropdown} className="profile-btn">
                <img
                  src={profilePic} // Dynamically use profilePic from localStorage
                  alt="Profile"
                  className="profile-pic"
                />
              </button>
              {dropdown && (
                <div className="dropdown">
                  <div className="header">
                    <p className="name">{username || 'User'}</p>
                  </div>
                  <Link to="/profile" className="item">Profile</Link>
                  <Link to="/settings" className="item">Settings</Link>
                  <button onClick={logout} className="item logout">Logout</button>
                </div>
              )}
            </div>
          ) : (
            <Link to="/login" className="login-btn">Login</Link>
          )}
        </div>
      </div>
    </nav>
  );
};


export default Navbar;
